import { StyleSheet, Text, View ,FlatList} from 'react-native';
import BannerMovies from '../../components/bannerFilmes';
import CardMovies from '../../components/cardFilmes';
import Header from '../../components/header';
import SearchBar from '../../components/searchbar';
import Filmes from '../../data/movies'

export default function App() {

  const[movies, setMovies] = useState([]);

  useEffect(()=>{
    async function buscarFilmes(){

      const response = await fetch('https://api.themoviedb.org/3/movie/now_playing?api_key=c1aa1a48466e116c7004b556908e3d2f&language=pt-br')
      const data = awaitresponse.json();
      console.log(data);
    }

    buscarFilmes();

  })

  return (
    <View style={styles.container}>
     <Header></Header>

     <SearchBar></SearchBar>

     <BannerMovies></BannerMovies>


     <View style = {{width :"90%"}}>
          <FlatList
            horizontal={true}
            showsHorizontalScrollIndicator={false}
            data={Filmes}
            keyExtractor={(item) => item.id}
            renderItem={({ item }) => (

              <CardMovies
                titulo={item.nome}
                imagem={item.imagem}
                nota={item.nota}
              />
            )}
          />
        </View>



    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#141a29',
    alignItems:'center'


  },
});
